def fix(string):
    return string.strip().replace('"',"'")
print('{')
for line in text.split('\n'):
    key, en, fr, pl = line.split('	')
    obj = '''\t{}: {{
\t\t"EN": "{}",
\t\t"FR": "{}",
\t\t"PL": "{}"
    }},'''.format(fix(key),fix(en),fix(fr),fix(pl))
    print(obj)
print('}')
